            <div class="col-12">
              <label for="address2" class="form-label">Department</label>
              <input type="text" class="form-control" id="address2" placeholder="Department"  [(ngModel)]="subject.department">
            </div>


        </div>

          <hr class="my-4">

          <button class="w-100 btn btn-primary btn-lg" type="submit" (click)="postDetailsOfSubject()">Save Subject Details</button>
